const form = document.querySelector("form");
form.addEventListener("submit", event => {
  event.preventDefault();
  const inscriptionId = document.querySelector("#inscriptionId").value;
  const url = `https://ordapi.xyz/inscription/${inscriptionId}`;

  fetch(url)
    .then(response => {
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      return response.json();
    })
    .then(inscriptionData => {
      // Display the inscription data in the "inscriptionInfo" div
      const inscriptionInfoDiv = document.querySelector("#inscriptionInfo");
      inscriptionInfoDiv.innerHTML = `
<h2>${inscriptionData.title}</h2>
<p>Address: ${inscriptionData["address"]}</p>
<img src="https://ordinals.com${inscriptionData["content"]}" alt="Inscription Image">
<div id="inscriptionInfo">
<h2></h2>
<p>Content Length: ${inscriptionData["content length"]}</p>
<p>Content Type: ${inscriptionData["content type"]}</p>
<p>Genesis Fee: ${inscriptionData["genesis fee"]}</p>
<p>Genesis Height: ${inscriptionData["genesis height"]}</p>
<p>Genesis Transaction: ${inscriptionData["genesis transaction"]}</p>
<p>ID: ${inscriptionData["id"]}</p>
<p>Location: ${inscriptionData["location"]}</p>
<p>Offset: ${inscriptionData["offset"]}</p>
<p>Output: ${inscriptionData["output"]}</p>
<p>Output Value: ${inscriptionData["output value"]}</p>
<p>Sat: ${inscriptionData["sat"]}</p>
<p>Timestamp: ${inscriptionData["timestamp"]}</p>
</div>

`;
    })
    .catch(error => {
      console.error("There was a problem with the fetch operation:", error);
    });
});
